<?phpnamespace App\Http\Controllers;use Illuminate\Http\Request;use Twilio\Rest\Client;use Twilio\Jwt\ClientToken;use Twilio\TwiML;class VideochatController extends Controller{	public function index(){		return view('videochat.index');	}	public function generate(Request $request, AccessToken $accessToken, IpMessagingGrant $ipmGrant)    {        $appName = "TwilioChat";        $deviceId = $request->input("device");        $identity = $request->input("identity");        $TWILIO_IPM_SERVICE_SID = config('app.twilio')['TWILIO_IPM_SERVICE_SID'];        $endpointId = $appName . ":" . $identity . ":" . $deviceId;        $accessToken->setIdentity($identity);        $ipmGrant->setServiceSid($TWILIO_IPM_SERVICE_SID);        $ipmGrant->setEndpointId($endpointId);        $accessToken->addGrant($ipmGrant);        $response = array(            'identity' => $identity,            'token' => $accessToken->toJWT()        );        return response()->json($response);    }}